import React from 'react';
import {useState,useEffect} from 'react';
import {io} from 'socket.io-client';
import {useLocation} from 'react-router-dom';
const socket=io("http://localhost:3009");
function Soc(){
    const locate=useLocation();
    const rom=locate.state;
    const [msg,setMsg]=useState([]);
    const [text,setText]=useState("");
    const [sorted,setSorted]=useState([]);
    function oon(e){
        setText(e.target.value);
    }
    function on(e){
        e.preventDefault();
        const tt={tex:text,time:new Date()};
        socket.emit("romsg",rom);
        socket.emit("msg",tt);
        setMsg((prev)=>[...prev,tt]);
        setSorted((prev)=>[...prev,msg]);
    }
    return(
        <>
         <div className="dd">
                <header className="hh"><h1 className="hd">Let's Chat</h1></header>
                <br/>
                {sorted.map((i,index)=>(
                    <div className="hhhh"><div className="hhhh1">{i.text}<footer className="fff"><small><sub>{i.time}</sub></small></footer></div></div>))}
                <div className="iii">
                    <input className="iiiii" type="text" name="input" onChange={oon}/>
                    <button className="bb" type="submit" onClick={on}>ðŸš€</button>
                </div>
            </div>
        </>
    )
}
export default Soc;
